// Room generation method
export function buildRoomFromArray(
    scene: Phaser.Scene,
    startX: number,
    startY: number,
    debugGraphics?: Phaser.GameObjects.Graphics
  ) {
    const worldLayer = (scene as any).worldLayer;
    const aboveLayer = (scene as any).aboveLayer;
  
    const placeTile = (tileIndex: number, x: number, y: number, layer: Phaser.Tilemaps.TilemapLayer) => {
      if (tileIndex !== 0) {
        const tile = layer.putTileAt(tileIndex, x, y);
        if (tile) tile.properties.collides = true;
      }
    };
  
    for (let y = 0; y < roomData.height; y++) {
      for (let x = 0; x < roomData.width; x++) {
        const index = y * roomData.width + x;
        // floor layer
        const floorTile = roomData.floorTiles[index];
        placeTile(floorTile, startX + x, startY + y, worldLayer);
  
        // object layer
        const objectTile = roomData.objectTiles[index];
        placeTile(objectTile, startX + x, startY + y, aboveLayer);
      }
    }
  
    worldLayer.setCollisionByProperty({ collides: true });
    aboveLayer.setCollisionByProperty({ collides: true });
  
    if (debugGraphics) {
      debugGraphics.clear();
      worldLayer.renderDebug(debugGraphics, {
        tileColor: null,
        collidingTileColor: new Phaser.Display.Color(255, 0, 0, 255),
        faceColor: new Phaser.Display.Color(0, 255, 0, 255),
      });
    }
  
    console.log(`Bi-level rooms are accomplished by array creation!Starting (${startX}, ${startY})`);
  }

export function createBuildRoomButton(scene: Phaser.Scene, startX = 0, startY = 20) {
  const buildRoomBtn = scene.add
    .rectangle(150, 400, 70, 30, 0x222222)
    .setScrollFactor(0)
    .setStrokeStyle(1, 0xffffff)
    .setInteractive();

  scene.add.text(120, 390, 'Build a room', {
    fontSize: '10px',
    color: '#ffffff',
  }).setScrollFactor(0);

  buildRoomBtn.on('pointerdown', () => {
    buildRoomFromArray(scene, startX, startY, (scene as any).debugGraphics);
    createOrUpdateGrid((scene as any).tilemap, true);
  });
}
